function setClearFields(){$("div.filter-container","tr.filters").bind("mouseover",function(){var e=$(this).find("input");if(e.val()){var t=e.offset();var n=$("<div />").addClass("clear-filter-field").css("z-index",e.css("z-index")+1).css("display","none").css("position","absolute").html('<a href="javascript:void(0)"><s class="icon-remove-sign icon-black"></s></a>').bind("click",function(){e.val("");$(this).fadeOut("fast");$(this).remove();e.change()}).insertAfter(e);n.css("left",t.left+e.width()-n.width()+5).css("top",t.top+e.height()/2-4).fadeIn("fast")}}).bind("mouseleave",function(){$("div.clear-filter-field").fadeOut("fast").remove()})}